			bs.operationtype = (CASE ce.refund
									WHEN 0 THEN N''Покупка''
									WHEN NULL THEN N''Покупка''
									WHEN 1 THEN N''Возврат''
								END) 
		FROM 
			@bonuscheques AS bs 
