				WHEN bs.chequeamount IS NULL THEN 0
			END AS [Сумма операции],
			bs.bonusAdded AS [Начислено бонусов],
			bs.bonusRedeemed AS [Списано бонусов],
			bs.proctime AS chequetime
		FROM
			@bonuscheques AS bs
			INNER JOIN card AS cd ON bs.card = cd.number
