				c.discount, 
				p.name AS partner, 
				pos.code AS pos, 
				c.card, 
				(SELECT SUM(bonus) FROM bonus WHERE cheque = c.id AND bonus > 0) AS added, 
				-(SELECT SUM(bonus) FROM bonus WHERE cheque = c.id AND bonus < 0) AS redeemed, 
				pos.name AS posname,
