				HAVING
					COUNT(ce.id) >= 10
			)
			SELECT @moreTenBuys = COUNT(number) FROM CTE;
		END

		IF(@operator IS NOT NULL AND @partner IS NULL AND @pos IS NULL)
		BEGIN
			WITH CTE AS
			(
				SELECT
