			(
				SELECT
					cd.number
				FROM
					clientoperator AS cr
					INNER JOIN card AS cd ON cr.client = cd.client AND cr.operator = cd.operator
					INNER JOIN cheque AS ce ON ce.card = cd.number
				WHERE
