				@avgCheque = COALESCE(AVG(amount), 0)
			FROM 
				cheque 
			WHERE 
				partner = @partner
				AND pos = @pos
				AND (refund = 0 OR refund IS NULL)
				AND (cancelled IS NULL OR cancelled = 0);

			SELECT 
