					AND (cancelled IS NULL OR cancelled = 0)
				GROUP BY
					DATEPART(WEEKDAY, ce.proctime)
			)
			SELECT	
				@buyWeekdaySum = (SELECT COALESCE(SUM(cte.chequesum), 0) FROM cte WHERE dayweek >= 2 AND dayweek <= 6),
