		@avgCheque NUMERIC(9,2) = NULL OUTPUT,
		@buysWeekdays INT = NULL OUTPUT,
		@buysWeekOff INT = NULL OUTPUT,
		@addedBonus NUMERIC(9,2) = NULL OUTPUT,
		@avgCharge NUMERIC(9,2) = NULL OUTPUT,
