					INNER JOIN cheque AS ce ON ce.card = cd.number
				WHERE
					cr.operator = @operator
					AND cr.pos = @pos
					AND (ce.refund = 0 OR ce.refund IS NULL)
					AND (ce.cancelled IS NULL OR ce.cancelled = 0)
