		DECLARE @buyWeekdaySum NUMERIC(9,2), @buyWeekOffAmount NUMERIC(9,2), @chequeSum NUMERIC(9,2),  @buyWeekdayQty NUMERIC(9,2), @buyWeekoffQty NUMERIC(9,2), @buyQty NUMERIC(9,2);

