	AS
	BEGIN
		IF NOT EXISTS(SELECT id FROM operator WHERE id = @operator)
		BEGIN
			SET @errormessage = N''Указанный оператор не найден''
			RETURN(1)
		END
		IF (@partner = 0)
		BEGIN
			SET @partner = NULL;
			SET @pos = NULL;
		END
