		END
		IF(@operator IS NOT NULL AND @operator > 0 AND @partner IS NULL AND @pos IS NULL)
		BEGIN
			SELECT
				cr.client,
				COALESCE(cr.surname + '' '', '''') + COALESCE(cr.name + '' '', '''') + COALESCE(cr.patronymic + '' '', '''') AS fullname,
				cr.phone,
