				AND (refund = 0 OR refund IS NULL)
				AND (cancelled IS NULL OR cancelled = 0);

			--@gainBuys NUMERIC(9,2),
			--@gainRefund NUMERIC(9,2)

			SELECT 
				@gainBuys = COALESCE(SUM(ABS(amount) - COALESCE(ABS(paidbybonus), 0)), 0)
			FROM 
				cheque 
