				AND (cancelled IS NULL OR cancelled = 0)
				AND (refund = 0 OR refund IS NULL);

			SELECT
				@gainRefund = COALESCE(SUM(ABS(amount) - COALESCE(ABS(paidbybonus), 0)), 0)
			FROM
