			)
			SELECT	
				@buyWeekdaySum = (SELECT COALESCE(SUM(cte.chequesum), 0) FROM cte WHERE dayweek >= 2 AND dayweek <= 6),
				@buyWeekOffAmount = (SELECT COALESCE(SUM(cte.chequesum), 0) FROM cte WHERE (dayweek = 1 OR dayweek = 7));

			WITH cte AS
			(
				SELECT
					COUNT(id) chequeQty,
