		END

		IF(@buyQty > 0)
		BEGIN
			SET @buysWeekdays = ROUND((@buyWeekdayQty/@buyQty)*100, 0);
			SET @buysWeekOff = ROUND((@buyWeekoffQty/@buyQty)*100, 0);
		END
