									SELECT 
										COUNT(DISTINCT ce.id) 
									FROM 
										bonus AS bs
										INNER JOIN cheque AS ce ON bs.cheque = ce.id
									WHERE 
										bs.operator = @operator 
										AND ce.partner = @partner
										AND ce.pos = @pos
										AND bs.bonus > 0 
										AND (bs.refund = 0 OR bs.refund IS NULL)
										AND (ce.cancelled IS NULL OR ce.cancelled = 0)
